
public class UsoOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=7, num2=5, resultado;
		
		resultado = num1 + num2;
		
		System.out.println("valores: " + num1 + "," + num2);
		
		System.out.println(" suma: " + resultado);
		
		resultado = num1 - num2;
		
		System.out.println(" resta: " + resultado);
		
		resultado = num1 * num2;
		
		System.out.println(" multipliacion: " + resultado);
		
		resultado = num1 / num2;
		
		System.out.println(" division: " + resultado);
		
		System.out.println("\n aplicamos incremento ++ a num2");
		
		num2+=2;
		
		resultado = num1 + num2;
		
		System.out.println("valores: " + num1 + "," + num2);
		
		System.out.println(" suma: " + resultado);
		
		resultado = num1 - num2;
		
		System.out.println(" resta: " + resultado);
		
		resultado = num1 * num2;
		
		System.out.println(" multipliacion: " + resultado);
		
		resultado = num1 / num2;
		
		System.out.println(" division: " + resultado);
		
		

	}

}
